import React,{useState} from 'react';
import {   Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem,Container} from 'reactstrap';
  import SecureLS from 'secure-ls';
import logo from '../assets/logo.png';
import cart from '../assets/cart.png';
import fb from '../assets/facebook.png';
import whatsapp from '../assets/whatsapp.png';
  import {
    Link
  } from "react-router-dom";
  import { useDispatch, useSelector } from 'react-redux';
  import { user, userdetail} from "../js/actions/index";
import { findByAltText } from '@testing-library/react';
  var ls = new SecureLS({encodingType: 'aes'});
  

const AppHeader = () => {
    const [isOpen, setIsOpen] = useState(false);
    const dispatch = useDispatch()
    const login = useSelector(state => state.user);
    const loginDetail = useSelector(state => state.userdetail);
    const toggle = () => setIsOpen(!isOpen);
    return (
     <Navbar  style={{color:"#FFFFFF",backgroundColor:'#005EB8'}} light expand="md">
        <NavbarBrand  style={{color:"#FFFFFF",fontWeight:"bold",fontFamily:"tahoma"}} to="/">
         Cow Mandi
          </NavbarBrand>
          <NavItem className="mr-auto">
              <Link style={{color:"#000",marginLeft:10,fontFamily:"sans-serif"}} to="/">
              Home
              </Link>
            </NavItem>
        <NavbarToggler onClick={toggle} />
        <Collapse isOpen={isOpen} navbar>
          
        {<Nav className="ml-auto"
        
         navbar>
        
  {
            login.length===0?
            
            loginDetail.role
           
            :
          <UncontrolledDropdown nav inNavbar>
            <NavItem>
              <DropdownToggle nav caret>
              <Link style={{color:"#000",marginLeft:10,fontFamily:"sans-serif"}} >Profile</Link>
              </DropdownToggle>
              </NavItem>
              <DropdownMenu right>
                <DropdownItem>
                  <Link  to="/Orders">My Orders</Link>
                </DropdownItem>
                  <DropdownItem>
                <Link  to="/Profile">Profile</Link>
                </DropdownItem>
                <DropdownItem divider />
                <DropdownItem>
                <Link  onClick={()=>{dispatch(user([]),userdetail([])) }} to="/login">Logout</Link>
                </DropdownItem>
              </DropdownMenu>
              
            </UncontrolledDropdown>
            }
          
            
            <a href="https://www.facebook.com/cowmandi.net" style={{fontFamily:"sans-serif",color:'white'}}>
                <img src={fb} alt="Logo" style={{fontFamily:"sans-serif",width:'20px',marginLeft:20}} />
                cowmandi.net
            </a>
              <a href="https://api.whatsapp.com/send?phone=+923432233434" style={{fontFamily:"sans-serif",color:'white'}}>
              
              <img src={whatsapp} alt="Logo" style={{fontFamily:"sans-serif",width:'20px',marginLeft:20}} >
            
              </img>
              +92 343 2233434
              </a>
              
            
           
            
            
          

          </Nav>}
        </Collapse>
      </Navbar>
     
    );
};

export default AppHeader;
