import React from "react";
import { Button,Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle,Container,Row,Col,List} from 'reactstrap';
  import SecureLS from 'secure-ls';
  import TreeMenu from 'react-simple-tree-menu';
  import CheckboxTree from 'react-checkbox-tree';
  import { enableRipple } from '@syncfusion/ej2-base';

  import { TreeViewComponent } from '@syncfusion/ej2-react-navigations';
  var ls = new SecureLS({encodingType: 'aes'});
  const token=ls.get('token')
  export default class Category extends React.Component {
    constructor(props) {
      super(props);
      this.state = {category:[],
                    product:[],
                    catId:0,
                    checked: [],
                    expanded: [],};
                   
                    this.countries = [
                      { id: 1, name: 'Australia', hasChild: true, expanded: true },
                      { id: 2, pid: 1, name: 'New South Wales', isChecked: true },
                      { id: 3, pid: 1, name: 'Victoria' },
                      { id: 4, pid: 1, name: 'South Australia' },
                      { id: 6, pid: 1, name: 'Western Australia', isChecked: true },
                      { id: 7, name: 'Brazil', hasChild: true },
                      { id: 8, pid: 7, name: 'Paraná' },
                      { id: 9, pid: 7, name: 'Ceará' },
                      { id: 10, pid: 7, name: 'Acre' },
                      { id: 11, name: 'China', hasChild: true },
                      { id: 12, pid: 11, name: 'Guangzhou' },
                      { id: 13, pid: 11, name: 'Shanghai' },
                      { id: 14, pid: 11, name: 'Beijing' },
                      { id: 15, pid: 11, name: 'Shantou' },
                      { id: 16, name: 'France', hasChild: true },
                      { id: 17, pid: 16, name: 'Pays de la Loire' },
                      { id: 18, pid: 16, name: 'Aquitaine' },
                      { id: 19, pid: 16, name: 'Brittany' },
                      { id: 20, pid: 16, name: 'Lorraine' },
                      { id: 21, name: 'India', hasChild: true },
                      { id: 22, pid: 21, name: 'Assam' },
                      { id: 23, pid: 21, name: 'Bihar' },
                      { id: 24, pid: 21, name: 'Tamil Nadu' },
                      { id: 25, pid: 21, name: 'Punjab' }
                  ];
                  this.field = { dataSource: this.countries, id: 'id', parentID: 'pid', text: 'name', hasChildren: 'hasChild' };
                  this.isChecked = true;
              }
              nodeChecked(args) {
                  alert("The checked node's id: " + this.checkedNodes); // To alert the checked node's id.
              
    }
    componentDidMount(){
    this.getCategory()
    }
    getCategory(){
      let data=[];
      fetch('https://www.weeklyfishclub.com/wp-json/wc/v3/products/categories?hide_empty=false', {method:'GET', 
        headers: {'Authorization': 'Bearer ' + token}})
        .then(response => response.json())
        .then(json => { //this.setState({category:json}); 
        console.log("json",json)  
        for(var a=0;a<= json.length-1;a++){
          let tempData1=[];
          console.log(json[a].name)
                for(var b=0;b<= json.length-1;b++){
                  console.log(json[b].name)
                      if(json[a].id==json[b].parent){
                          let tempData2=[];
                            
                            for(var c=0;c<= json.length-1;c++){
                              console.log(json[c].name)
                                      if(json[b].id==json[c].parent){
                                            tempData2.push({'value':json[c].id,
                                                            'label':json[c].name,
                                                            })
                                            json.splice(c, 1); 
                                      }
                            } 
                                  tempData1.push({'value':json[b].id,
                                                  'label':json[b].name,
                                                  'children':tempData2})
                              json.splice(b, 1); 
                      }
                    
                  }

          data.push({'value':json[a].id,
                      'label':json[a].name,
                      'children':tempData1,
                      })
          
         }
         console.log("array",data)  
         this.setState({category:data})
       });
    }
    getProduct(id){
      const token=ls.get('token')
      const {catId}=this.state;
      
      fetch(`https://www.weeklyfishclub.com/wp-json/wc/v3/products/?category=${id}`, {
        method:'GET', 
        headers: {'Authorization': 'Bearer ' + token}})
        .then(response => response.json())
        .then(json => {
          if(json.length>0){
            
     
          //   this.setState({product:json});
            }
          else{this.setState({product:[]});} 
        
       });
    }
    
    render() {
      const nodes = [{
        value: 'mars',
        label: 'Mars',
        children: [
            { value: 'phobos', label: 'Phobos' },
            { value: 'deimos', label: 'Deimos' },
        ],
    }];
       return (
          <div style={{marginTop:20}}>
            <Container className="themed-container" fluid="sm" >
           <Row>
            <Col md="4">
            
              <h5>Category</h5>
              <TreeViewComponent fields={this.field} showCheckBox={this.isChecked} nodeChecked={this.nodeChecked}/>
                  <CheckboxTree
                    nodes={this.state.category}
                    checked={this.state.checked}
                    expanded={this.state.expanded}
                    onCheck={checked => this.setState({ checked })}
                    onExpand={expanded => this.setState({ expanded })}
                    onClick={(e)=>{this.getProduct(e.value)}}
                />
            </Col>
              <Col>
              <h5>Fishes</h5><Row>
              
                {this.state.product.map((product) =>  <Col sm="4">
                
                  <Card>
                    <CardBody  style={{backgroundColor: "#f6f6f6"}}>
                      {product.images[0]?
                    <CardImg onClick={()=>{this.setParam(product.id)}} top width="20%" style={{width:200,height:150}} src={product.images[0].src} alt="Fish" />
                      :<div></div>}
                      <CardTitle tag="h5" >{product.slug} </CardTitle>
                    <CardTitle tag="h6" color="blue">Rs. {product.price}</CardTitle>
                        
                </CardBody>
              </Card></Col>)}
              </Row>
            </Col>
            </Row>
          </Container>
          </div>
       );
    }
 }
